import React, { useState, useEffect } from 'react';
import api from '../services/api';

const Quiz = () => {
  const [quiz, setQuiz] = useState('');

  useEffect(() => {
    const fetchQuiz = async () => {
      const response = await api.post('/quiz');
      setQuiz(response.data.quiz);
    };

    fetchQuiz();
  }, []);

  return (
    <div>
      <h2>Quiz</h2>
      <p>{quiz}</p>
    </div>
  );
};

export default Quiz;
