import React from 'react';

const Quiz = ({ quiz }) => {

  const renderQuestions = () => {
    if (!quiz || !quiz.questions) return null;
    try {
      // The questions are stored as a JSON string in the database
      const questions = JSON.parse(quiz.questions);
      return questions.map((q, index) => (
        <div key={index} className="mt-4">
          <p className="font-medium">{index + 1}. {q.split('\n')[0]}</p>
          <div className="flex flex-col mt-2 space-y-2">
            {q.split('\n').slice(1).map((choice, choiceIndex) => (
              <label key={choiceIndex} className="flex items-center">
                <input type="radio" name={`question-${index}`} value={choice} className="mr-2" />
                <span>{choice}</span>
              </label>
            ))}
          </div>
        </div>
      ));
    } catch (e) {
      console.error("Failed to parse quiz questions", e);
      return <p className="text-red-500">Error displaying quiz questions.</p>;
    }
  };

  return (
    <div className="p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-xl font-bold mb-4 text-gray-800">Comprehension Quiz</h2>
      {!quiz ? (
        <p className="text-gray-500">No quiz available. Ask the tutor to generate one after a story!</p>
      ) : (
        <div>
          <h3 className="text-lg font-semibold text-blue-600">{quiz.title}</h3>
          <form>
            {renderQuestions()}
            <button type="submit" className="mt-6 w-full p-2 bg-green-500 text-white rounded-md hover:bg-green-600">
              Submit Answers
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default Quiz;
